getQuestions = function(){
  questions = NULL
  ib = fromJSON(item_bank)
  if(ib$table_type == "custom") {
    test_id = concerto$mainTest$id
    table = ib$custom_table
    question_column = ib$custom_question_column$column
    response_options_column = ib$custom_response_options_column$column
    a_column = ib$custom_a_column$column
    b_column = ib$custom_b_column$column
    c_column = ib$custom_c_column$column
    d_column = ib$custom_d_column$column
    correct_column = ib$custom_correct_column$column
    test_id_column = ib$custom_test_id_column$column
    cb_group_column = ib$custom_cb_group_column$column
    questions = concerto.table.query("SELECT id, `{{question_column}}` AS `question`, `{{response_options_column}}` AS `response_options`, `{{a_column}}` AS `a`, `{{b_column}}` AS `b`, `{{c_column}}` AS `c`, `{{d_column}}` AS `d`, `{{correct_column}}` AS `correct`, `{{cb_group_column}}` AS `cb_group` FROM `{{table}}` WHERE `{{test_id_column}}` = {{test_id}}", list(
      question_column=question_column,
      response_options_column=response_options_column,
      a_column=a_column,
      b_column=b_column,
      c_column=c_column,
      d_column=d_column,
      correct_column=correct_column,
      cb_group_column=cb_group_column,
      table=table,
      test_id_column=test_id_column,
      test_id=test_id
    ))
  }
  if(ib$table_type == "direct") {
    if(length(ib$direct_table) > 0) {
      for(i in 1:length(ib$direct_table)) {
        ib$direct_table[[i]]$response_options = toJSON(ib$direct_table[[i]]$response_options) #response options don't fit into flat table, so turn them back to JSON.
        questions = rbind(questions, data.frame(ib$direct_table[[i]], stringsAsFactors=F))
      }
    }
  }

  if(dim(questions)[1] == 0) { stop("Item bank must not be empty!") }
  return(questions)
}

getContent = function(question) {
  content = paragraph

  content = paste0(content, "<div class='formField'>")
  content = paste0(content, "<div class='cat_question'>", question$question, "</div>")
  content = paste0(content, getResponseOptions(question))
  content = paste0(content, "</div>")

  return(content)
}

getResponseOptions = function(question) {
  content = ""
  options = NULL
  o = question$response_options
  if(is.character(o)) { o = fromJSON(o) }
  if(length(o) > 0) {
    #response options
    for(i in 1:length(o)) {
      options = rbind(options, as.list(o[[i]]))
    }

    if(randomize_response_options == "1") {
      if(dim(options)[1] > 0) {
        options = options[sample(1:dim(options)[1]),]
      }
    }

    content = "<div class='catResponseGroup'>"
    section_class = "catResponseSectionVertical"
    section_styles = ""
    if(responses_layout == "horizontal") { 
      section_class = "catResponseSectionHorizontal" 
      section_styles = paste0("width: ",response_width)
    }

    for(i in 1:dim(options)[1]) {
      option = options[i,]
      content = paste0(content, "<div class='",section_class,"' style='",section_styles,"'><label for='input_response_",i,"'><table class='catResponseTable'><tr>")
      content = paste0(content, "<td class='catResponseButtonCell'><input type='radio' id='input_response_",i,"' name='response' value='",option$value,"' /></td>")
      content = paste0(content, "<td class='catResponseLabelCell'>",option$label,"</td>")
      content = paste0(content, "</tr></table></label></div>")
    }
    content = paste0(content, "<div style='clear:both;'></div>")
    content = paste0(content, "</div>")

  } else {
    #open question
    content = paste0("<div><input type='text' name='response' style='width:100%;' /></div>")
  }
  return(content)
}

shouldStop = function(response, sem, out, questions) {
  sr_time = fromJSON(stopping_time)
  if(!is.null(sr_time$type) && sr_time$type == "test" && response$isTimeout == "1") { return(TRUE) }

  sr_count = fromJSON(stopping_item_count)
  if((sr_count$enabled == "1" && length(out) >= as.numeric(sr_count$count)) || length(out) >= dim(questions)[1]) { return(TRUE) }

  sr_accuracy = fromJSON(stopping_accuracy)
  if(sr_accuracy$enabled == "1" && as.numeric(sr_accuracy$accuracy) >= sem ) { return(TRUE) }
  return(FALSE)
}

saveResponse = function(response, question, rb, theta, sem, correct) {
  table = "default_cat_response_table"
  item_id_column = "item_id"
  response_column = "response"
  time_taken_column = "time_taken"
  session_internal_id_column = "session_internal_id"
  correct_column = "correct"
  theta_column = "theta"
  sem_column = "sem"
  if(rb$table_type == "custom") {
    table = rb$custom_table
    item_id_column = rb$custom_item_id_column$column
    response_column = rb$custom_response_column$column
    time_taken_column = rb$custom_time_taken_column$column
    session_internal_id_column = rb$custom_session_internal_id_column$column
    correct_column = rb$custom_correct_column$column
    theta_column = rb$custom_theta_column$column
    sem_column = rb$custom_sem_column$column
  }
  session_internal_id = 0
  if(!is.null(session) && is.list(session)) {
    session_internal_id = session$internal_id
  } else {
    session_internal_id = concerto$session$id
  }

  concerto.table.query("INSERT INTO `{{table}}` SET `{{item_id_column}}`={{item_id}}, `{{response_column}}`='{{response}}', `{{time_taken_column}}`={{time_taken}}, `{{session_internal_id_column}}`={{session_internal_id}}, `{{correct_column}}`={{correct}}, `{{theta_column}}`={{theta}}, `{{sem_column}}`={{sem}}", list(
    table=table,
    session_internal_id_column=session_internal_id_column,
    session_internal_id=session_internal_id,
    item_id_column=item_id_column,
    item_id=question$id,
    response_column=response_column,
    response=response$response,
    time_taken_column=time_taken_column,
    time_taken=response$timeTaken,
    correct_column=correct_column,
    correct=correct,
    theta_column=theta_column,
    theta=theta,
    sem_column=sem_column,
    sem=sem
  ))
}

getTemplateParams = function(template_params){
  params = list()
  if(!is.null(inserts) && is.list(inserts)) {
    params = inserts
  }
  for(key in ls(template_params)) {
    params[[key]] = template_params[[key]]
  }
  return(params)
}

getNextDebugItem = function(response, questions) {
  for(i in 1:dim(questions)[1]) {
    question = questions[i,]
    if(question$id == response$item_debug) {
      return(i)
    }
  }
  return(NULL)
}

getDebugContent = function(content, question) {
  debug = "<div class='debug'>"
  debug = paste0(debug, "<p>Current item id: #",question$id,"</p>") 
  debug = paste0(debug, "<p>Next item id: <input type='text' name='item_debug' /></p>")
  debug = paste0(debug, "</div>")
  content = paste0(debug, content)
  return(content)
}

