guessCsvType = function(csv) {
  #guesses CSV type, 1 for regular, 2 for e.g. german locale
  type = 1
  header = readLines(csv,n=1)
  count2 = 0
  count2_elems = strsplit(header,";")
  if(length(count2_elems) == 1) {
  	count2 = length(count2_elems[[1]])
  }
  count1 = 0
  count1_elems = strsplit(header,",")
  if(length(count1_elems) == 1) {
  	count1 = length(count1_elems[[1]])
  }
  if(count2 > count1) {
    type = 2
  } else if(count2 == count1){
    print("Can not guess CSV type. Assuming type 1.")
  }
  print(paste0("CSV type: ",type))
  return(type)
}

validateField = function(name, value, columns) {
  #validates field
  if(is.null(value) || is.na(value)) {
    error_message <<- paste0("CSV field ",name," has missing value!")
    print(error_message)
    return(F)
  }

  type = NULL
  for(i in 1:dim(columns)[1]) {
    if(columns[i,"Field"] == name) {
      type = columns[i,"Type"]
      break
    }
  }
  if(is.null(type)) {
    error_message <<- paste0("CSV field ",name," doesn't have data table equivalent!")
    print(error_message)
    return(F)
  }
  shouldBeNumeric = F
  for(i in 1:length(numericDataTypes)) {
    if(grepl(numericDataTypes[i],type)) {
      shouldBeNumeric = T
      break
    }
  }
  numericValue = as.numeric(value)
  if(is.na(numericValue)) {
    error_message <<- paste0("CSV field ",name," has invalid numeric value!")
    print(error_message)
    return(F)
  }
  return(T)
}

