getQuestions = function(){
  questions = NULL
  ib = fromJSON(item_bank)
  if(ib$table_type == "custom") {
    table = ib$custom_table$table
    question_column = ib$custom_table$columns$question
    response_options_column = ib$custom_table$columns$response_options
    irt_discrimination_column = ib$custom_table$columns$irt_discrimination
    cb_group_column = ib$custom_table$columns$cb_group
    questions = concerto.table.query("SELECT id, `{{question_column}}` AS `question`, `{{response_options_column}}` AS `response_options`, `{{irt_discrimination_column}}` AS `irt_discrimination`, `{{cb_group_column}}` AS `cb_group` FROM `{{table}}`", list(
      question_column=question_column,
      response_options_column=response_options_column,
      irt_discrimination_column=irt_discrimination_column,
      cb_group_column=cb_group_column,
      table=table
    ))
  }
  if(ib$table_type == "direct") {
    if(length(ib$direct_table) > 0) {
      for(i in 1:length(ib$direct_table)) {
        ib$direct_table[[i]]$response_options = toJSON(ib$direct_table[[i]]$response_options) #response options don't fit into flat table, so turn them back to JSON.
        questions = rbind(questions, data.frame(ib$direct_table[[i]], stringsAsFactors=F))
      }
    }
  }

  if(dim(questions)[1] == 0) { stop("Item bank must not be empty!") }
  return(questions)
}


getContent = function(question) {

  content = paragraph #don't know what this does!
  content = paste0(content, "<table class='polyCATResponseTable'>")
  content = paste0(content, "<tr>")
  content = paste0(content, "<th class='polyCATHeaderCell'></th>")

  # add other header and then response cells based on how many response options there are.
  options = NULL
  o = question$response_options
  if(is.character(o)) { o = fromJSON(o) }
  if(length(o) > 0) {
    #response options
    for(i in 1:length(o)) {
      options = rbind(options, as.list(o[[i]]))
    }

    if(randomize_response_options == "1") {
      if(dim(options)[1] > 0) {
        options = options[sample(1:dim(options)[1]),]
      }
    }

    #add the table headings with the option labels
    for(i in 1:dim(options)[1]) {
      option = options[i,]
      content = paste0(content, "<th class='polyCATHeaderCell'>",option$label,"</th>")
    }

    #next line in the table - add the question
    content = paste0(content, "</tr><tr><td class='polyCATQuestionCell'>", question$question, "</td>")                   

    #add the response radio options to the table.
    for(i in 1:dim(options)[1]) {
      option = options[i,]
      content = paste0(content, "<td class='polyCATresponseButtonCell'><label for='input_response_",i,"' class='polyCATLabel'><input type='radio' id='input_response_",i,"' name='response' value='",option$value,"' /></label></td>")
    }

    content = paste0(content, "</tr></table>")



    return(content)

  } else {
    stop("Question does not have any response options")
    #open question
    #content = paste0("<div><input type='text' name='response' style='width:100%;' /></div>")
  }


}

# when the user responds to an item, it returns the Value of their response. We need to take this value and then find out what response column it refers to in the item bank (i.e. response 0? response 4?)
getResponseNumber = function(response = NULL, questions) {
  print("matching response number")
  print(response)
  options = NULL
  o = question$response_options
  if(is.character(o)) { o = fromJSON(o) }
  if(length(o) > 0) {
    #response options
    for(i in 1:length(o)) {
      options = rbind(options, as.list(o[[i]]))
    }

    for(i in 1:dim(options)[1]) {
      option = options[i,]
      if (is.null(response)) { #user did not respond to the question. Presumably the time ran out or something like that. In that case, just take the middle option.
        print(paste0("Warning: User did not respond to the question. Taking option ", round((dim(options)[1] + 0.1) / 2, 0), " as their response.")) #here the first response is number 1. below it's 0 which is why we do -1.
        return (round((dim(options)[1] + 0.1) / 2, 0) - 1) #do rounding because there might be an odd number of responses. R rounding takes 0.5 down to 0, so add 0.1 so that it's 0.6 and goes up to 1.
      } else if (option$value == response) { #have we found the correct response option?
        return (i - 1) #i - 1 because catR wants the responses to start from 0. 
      }
    }

    stop("User's response did not match any of the options in the database")

  } else {
    stop("Question does not have any response options")
  }

}

shouldStop = function(response, sem, out, questions) {
  sr_time = fromJSON(stopping_time)
  if(!is.null(sr_time$type) && sr_time$type == "test" && response$isTimeout == "1") { return(TRUE) }

  sr_count = fromJSON(stopping_item_count)
  if((sr_count$enabled == "1" && length(out) >= as.numeric(sr_count$count)) || length(out) >= dim(questions)[1]) { return(TRUE) }

  sr_accuracy = fromJSON(stopping_accuracy)
  print(sr_accuracy)
  print(sr_accuracy$enabled)
  print(as.numeric(sr_accuracy$accuracy))
  print(sem)
  if(sr_accuracy$enabled == "1" && as.numeric(sr_accuracy$accuracy) >= sem ) { return(TRUE) }
  return(FALSE)
}

