getContent = function(login_form) {
  content = login_form$paragraph

  if(!is.na(type) && type == "table") {
    login_control = concerto.template.join(templateId=login_form$form_field_input, params=list(id="login",name="login",type="text",value=""))
    password_control = concerto.template.join(templateId=login_form$form_field_input, params=list(id="password",name="password",type="password",value=""))
    login = concerto.template.join(templateId=login_form$form_field, params=list(id="login",label="login",classes="",control=login_control))
    password = concerto.template.join(templateId=login_form$form_field, params=list(id="password",label="password",classes="",control=password_control))
    content = paste0(content, login, password)
  }

  return(content)
}

authorizeUser = function(response) {
  print(paste0("authorizing user: ",response$login))
  ub = fromJSON(user_bank)

  if(!is.na(type) && type == "table") {
    if(ub$table_type == "direct") {
      users = ub$direct_table
      if(length(users) > 0) {
        for(i in 1:length(users)) {
          user = users[[i]]
          hashed_pass = response$password
          if(user$encryption != "plain") {
            hashed_pass = digest(hashed_pass, user$encryption, serialize=F)
          }
          if(user$login == response$login && user$password == hashed_pass && user$enabled == 1) {
            rm(users)
            return(user)
          }
        }
      }
      rm(users)
    } 
    if(ub$table_type == "default") {
      users = concerto.table.query("SELECT * FROM default_user_table WHERE data_set_id = '{{data_set_id}}'", list(
        data_set_id = dbEscapeStrings(concerto$connection, toString(ub$data_set_id))
      ))
      if(dim(users)[1] > 0) {
        for(i in 1:dim(users)[1]) {
          user = as.list(users[i,])
          hashed_pass = response$password
          if(user$encryption != "plain") {
            hashed_pass = digest(hashed_pass, user$encryption, serialize=F)
          }
          if(user$login == response$login && user$password == hashed_pass && user$enabled == 1) {
            rm(users)
            return(user)
          }
        }
      }
      rm(users)
    } 
    if(ub$table_type == "custom") {
