createSession = function(user) {
  sb = fromJSON(session_bank)
  user_id = NULL
  if(!is.null(user)) { user_id = user$id }

  if(sb$table_type == "default") {
    concerto.table.query("INSERT INTO `default_session_table` SET `internal_id` = '{{internal_id}}', `test_id` = {{test_id}}, `user_id` = '{{user_id}}', `last_activity_time`= NOW()", list(
      internal_id=concerto$session$id, 
      test_id=concerto$mainTest$id, 
      user_id=user_id
    ))
  } 
  if(sb$table_type == "custom") {
    concerto.table.query("INSERT INTO `{{table}}` SET `{{iternal_id_column}}` = '{{internal_id}}', `{{test_id_column}}` = {{test_id}}, `{{user_id_column}}` = '{{user_id}}', `{{last_activity_time_column}}` = NOW()", list(
      table=sb$custom_table$table, 
      iternal_id_column=sb$custom_table$columns$session_internal_id, 
      internal_id=concerto$session$id, 
      test_id_column=sb$custom_table$columns$test_id, 
      test_id=concerto$mainTest$id, 
      user_id_column=sb$custom_table$columns$user_id, 
      user_id=user_id,
      last_activity_time_column=sb$custom_table$columns$last_activity_time
    ))
  }
  return(list(id=concerto.table.lastInsertId(), test_id=concerto$mainTest$id, user_id=user_id, internal_id=concerto$session$id))
}

resumeSession = function(user) {
  sb = fromJSON(session_bank)
  user_id = NULL
  if(!is.null(user)) { user_id = user$id }

  table = "default_session_table"
  test_id_column = "test_id"
  user_id_column = "user_id"
  internal_id_column = "internal_id"
  last_activity_time_column = "last_activity_time"
  finished_column = "finished"
  if(sb$table_type == "custom") {
    table = sb$custom_table$table
    test_id_column = sb$custom_table$columns$test_id
    user_id_column = sb$custom_table$columns$user_id
    internal_id_column = sb$custom_table$columns$internal_id
    last_activity_time_column = sb$custom_table$columns$last_activity_time
    finished_column = sb$custom_table$columns$finished
  }

  session = concerto.table.query("SELECT * FROM `{{table}}` WHERE `{{test_id_column}}` = {{test_id}} AND `{{user_id_column}}` = '{{user_id}}' AND `{{finished_column}}` = 0 ORDER BY `id` DESC LIMIT 1", list(
      table=table, 
      test_id_column=test_id_column, 
      test_id=concerto$mainTest$id, 
      user_id_column=user_id_column, 
      user_id=user_id,
      finished_column=finished_column
  ))
  if(dim(session)[1] == 0) {
    return(NULL)
  }
  session = as.list(session)
  session$previous_internal_id = session$internal_id
  session$internal_id = concerto$session$id

  time_limit = as.numeric(resume_session_time_limit)
  if(time_limit > 0) {
  	time_diff = as.numeric(Sys.time()) - as.numeric(strptime(session$last_activity_time, "%Y-%m-%d %H:%M:%S"))
    if(time_diff > time_limit) {
      print("session resume time limit exceeded")
      return(NULL)
    }
  }

  concerto.table.query("UPDATE `{{table}}` SET `{{internal_id_column}}`='{{internal_id}}', `{{last_activity_time_column}}`=NOW() WHERE `id`={{id}}", list(
    table=table,
    internal_id_column=internal_id_column,
    internal_id=concerto$session$id,
    id=session$id,
    last_activity_time_column=last_activity_time_column
  ))

  return(session)
}

doesLoginExists = function(login) {
  ub = fromJSON(user_bank)

