      users = concerto.table.query("SELECT * FROM default_user_table WHERE data_set_id = '{{data_set_id}}' AND login = '{{login}}'", list(
        data_set_id=dbEscapeStrings(concerto$connection, toString(ub$data_set_id)),
        login=dbEscapeStrings(concerto$connection, toString(login))
      ))
      if(dim(users)[1] > 0) {
