isValid = function(type, value, condition, form, response) {
  valid = T
  if(!is.null(response)) {
    env_vld = new.env()
    if(length(form) > 0) {
      for(i in 1:length(form)) {
        v = response[[form[[i]]$name]]
        assign(form[[i]]$name, v, envir=env_vld)
      }
    }

    res = try(eval(parse(text=condition), envir=env_vld), T)
    if(!is.null(res) && res == T) {
      valid = T
    }
    if(!is.null(res) && res == F) {
      valid = F
    }
  }
  return(valid)
}

