      } else if(type == "slider") {
        if(value == "") {
          value = "null";
        }
        control = concerto.template.join(templateId=form_field_slider_template,params=list(id=id,name=name,value=value,definition=toJSON(form[[i]]$definition)))
      }
        classes = ""
        if(!is.null(response) && !valid) {
          classes = "validationHighlight"
        }
        field = concerto.template.join(templateId=form_field_template,params=list(id=id,label=label,classes=classes,control=control))
        content = paste0(content, field)
        }
        }
        return(content)
        }

        getAlert = function(form, response) {
          alert = NULL
          if(response$buttonPressed != "submit") {
            return(alert)
          }

          if(length(form) > 0) {
            for(i in 1:length(form)) {
              type = form[[i]]$definition$type
              value = response[[form[[i]]$name]]
              condition = form[[i]]$validation
              valid = isValid(type, value, condition, form, response)

              validationHighlight = ""
              if(!is.null(response) && !valid) {
                alert = concerto.template.join(templateId=form_alert_template,params=list(alert=form[[i]]$alert))
              }
            }
          }
          return(alert)
        }

        getTemplateParams = function(template_params){
          params = list()
          if(!is.null(inserts) && is.list(inserts)) {
            params = inserts
          }
          for(key in ls(template_params)) {
            params[[key]] = template_params[[key]]
          }
          return(params)
        }

        cb1 = fromJSON(custom_button_1)
        buttons = concerto.template.join(templateId=buttons_template,params=list(
          label=button_label,
          cb1_enabled=cb1$enabled,
          cb1_label=cb1$label
        ))

        content = paragraph
        form = fromJSON(form)

        if(!is.null(warning_msg) && (is.na(warning_msg) || warning_msg == "")) {
          warning_msg = ""
        }

        response = NULL
        while(T) {
          content = getContent(paragraph, form, response)
          response = concerto.template.show(decoded_template_def$layout, params=getTemplateParams(list(
            header=decoded_template_def$header, 
            title=title,
            content=content, 
            warning_msg=warning_msg, 
            buttons=buttons,
            footer=decoded_template_def$footer
          )))
          warning_msg = getAlert(form, response)
          if(is.null(warning_msg) || warning_msg == "") { break }
        }

        if(response$buttonPressed == "submit") {
          .branch = "submit"
        } else if(response$buttonPressed == "custom_button_1") {
          .branch = "custom_button_1"
        }

