getQuestions = function(){
  questions = NULL
  ib = fromJSON(item_bank)
  if(ib$table_type == "custom") {
    data_set_id = ib$data_set_id
    table = ib$custom_table$table
    question_column = ib$custom_table$columns$question
    response_options_column = ib$custom_table$columns$response_options
    a_column = ib$custom_table$columns$a
    b_column = ib$custom_table$columns$b
    c_column = ib$custom_table$columns$c
    d_column = ib$custom_table$columns$d
    correct_column = ib$custom_table$columns$correct
    data_set_id_column = ib$custom_table$columns$data_set_id
    cb_group_column = ib$custom_table$columns$cb_group
    questions = concerto.table.query("SELECT id, `{{question_column}}` AS `question`, `{{response_options_column}}` AS `response_options`, `{{a_column}}` AS `a`, `{{b_column}}` AS `b`, `{{c_column}}` AS `c`, `{{d_column}}` AS `d`, `{{correct_column}}` AS `correct`, `{{cb_group_column}}` AS `cb_group` FROM `{{table}}` WHERE `{{data_set_id_column}}` = '{{data_set_id}}'", list(
      question_column=question_column,
      response_options_column=response_options_column,
      a_column=a_column,
      b_column=b_column,
      c_column=c_column,
      d_column=d_column,
      correct_column=correct_column,
      cb_group_column=cb_group_column,
      table=table,
      data_set_id_column=data_set_id_column,
      data_set_id=dbEscapeStrings(concerto$connection, toString(data_set_id))
    ))
  }
  if(ib$table_type == "direct") {
    if(length(ib$direct_table) > 0) {
      for(i in 1:length(ib$direct_table)) {
        ib$direct_table[[i]]$response_options = toJSON(ib$direct_table[[i]]$response_options) #response options don't fit into flat table, so turn them back to JSON.
        questions = rbind(questions, data.frame(ib$direct_table[[i]], stringsAsFactors=F))
      }
    }
  }

  if(dim(questions)[1] == 0) { stop("Item bank must not be empty!") }
  return(questions)
}

getContent = function(question) {
  o = question$response_options
  if(is.character(o)) { o = fromJSON(o) }
  response_type = "open_text"
  options = NULL
  if(length(o) > 0) {
    response_type = "multiple_choice"
    if(randomize_response_options == "1") {
      options = o[sample(1:length(o))]
    } else {
      options = o
    }
    section_classes = "CAT-response-section-vertical"
    section_styles = ""
    if(responses_layout == "horizontal") { 
      section_classes = "CAT-response-section-horizontal" 
      section_styles = paste0("width: ",response_width)
    }
  }

  content = concerto.template.join(templateId="CAT_content",params=list(
    paragraph = paragraph,
    question = question$question,
    response_type = response_type,
    response_options = toJSON(options),
    section_classes = section_classes,
    section_styles = section_styles
  ))

  return(content)
}

shouldStop = function(response, sem, out, questions) {
  sr_time = fromJSON(stopping_time)
  if(!is.null(sr_time$type) && sr_time$type == "test" && response$isTimeout == "1") { return(TRUE) }

  sr_count = fromJSON(stopping_item_count)
  if((sr_count$enabled == "1" && length(out) >= as.numeric(sr_count$count)) || length(out) >= dim(questions)[1]) { return(TRUE) }

  sr_accuracy = fromJSON(stopping_accuracy)
  if(sr_accuracy$enabled == "1" && as.numeric(sr_accuracy$accuracy) >= sem ) { return(TRUE) }
  return(FALSE)
}

