    time_taken_column = rb$custom_table$columns$time_taken
    session_id_column = rb$custom_table$columns$session_id
    correct_column = rb$custom_table$columns$correct
    theta_column = rb$custom_table$columns$theta
    sem_column = rb$custom_table$columns$sem
    data_set_id_column = rb$custom_table$columns$data_set_id
  }
  session_id = 0
  if(!is.null(session) && is.list(session)) {
    session_id = session$id
  } else {
    session_id = paste0("i",concerto$session$id)
  }

  concerto.table.query("INSERT INTO {{table}} ({{item_id_column}}, {{response_column}}, {{time_taken_column}}, {{session_id_column}}, {{correct_column}}, {{theta_column}}, {{sem_column}}, {{data_set_id_column}}) VALUES ({{item_id}}, '{{response}}', {{time_taken}}, '{{session_id}}', {{correct}}, {{theta}}, {{sem}}, '{{data_set_id}}')", list(
