  concerto$promoted$.savedState$CAT$out <<- out
  concerto$promoted$.savedState$CAT$time_limit <<- time_limit
  concerto$promoted$.savedState$CAT$correctness <<- correctness
  concerto$promoted$.savedState$CAT$current_item <<- current_item
}

