  sem = 1
  out = NULL
  time_limit = 0
  if(!is.null(sr_time$type) && (sr_time$type == "test" || sr_time$type == "item")) { time_limit = as.numeric(sr_time$time_limit) }
  correctness = c()
  current_item = 1
}

