cb_control = NULL
cb = fromJSON(cb)
print(cb)
if(cb$enabled == "1") {
  cb_group = as.character(questions[,9])
  print(cb_group)

  props_list = NULL
  if(length(cb$props) > 0) {
    for(i in 1:length(cb$props)) {
      props_list = rbind(props_list, as.list(cb$props[[i]]))
    }
  } else {
    stop("Missing content balancing settings!")
  }
  cb_control = list(
    names=as.character(props_list[,1]),
    props=as.numeric(props_list[,2])
  )
  print(cb_control)
