param_bank = questions[,4:7,drop=F]
param_bank = apply(param_bank, 2, as.numeric)
rb = fromJSON(response_bank)
response = NULL
warning_msg = ""
timer_set = FALSE
if(time_limit > 0) { timer_set = TRUE }
buttons = concerto.template.join(templateId=buttons_template,params=list(label=button_label))

if(item_debug == "0" && !resumed) { 
  current_item = nextItem(param_bank, theta=theta, out=out, criterion=next_item_criterion, method=scoring_method, randomesque=randomesque, cbGroup=cb_group, cbControl=cb_control)$item
}
while(T) {
  question = questions[current_item,]
  content = getContent(question)

  if(item_debug == "0") {
    updateSavedState(questions, theta, sem, out, time_limit, correctness, current_item)
    response = concerto.template.show(decoded_template_def$layout, params=getTemplateParams(list(
      header=decoded_template_def$header, 
      title=title,
      content=content, 
      warning_msg=warning_msg, 
      buttons=buttons,
      footer=decoded_template_def$footer
    )), timeLimit=time_limit)
    warning_msg = ""

    if(timer_set) {
      time_limit = time_limit - as.numeric(response$timeTaken)
    }
    valid = "response" %in% ls(response) && response$response != ""
    if(valid || response$isTimeout == "1") {
      correct = 0
      if(!is.null(response$response) && response$response == question$correct) { correct = 1 }
      correctness = c(correctness, correct)
      out = c(out, current_item)
      theta <- thetaEst(matrix(param_bank[out,], ncol=4, byrow=F), correctness, method=scoring_method)
      sem <- semTheta(theta, matrix(param_bank[out,], ncol=4, byrow=F), correctness)
      saveResponse(response, question, rb, theta, sem, correct)
      if(shouldStop(response, sem, out, questions)) {
        if(response$isTimeout == "1") {
          .branch = "out_of_time"
        }
        break
      } else {
        if(timer_set && sr_time$type == "item") {
          time_limit = as.numeric(sr_time$time_limit)
        }
        current_item = nextItem(param_bank, theta=theta, out=out, criterion=next_item_criterion, method=scoring_method, randomesque=randomesque, cbGroup=cb_group, cbControl=cb_control)$item
      }
    } else {
      warning_msg = no_answer_alert
    }
  } else {
    content = getDebugContent(content, question)
    updateSavedState(questions, theta, sem, out, time_limit, correctness, current_item)
    response = concerto.template.show(decoded_template_def$layout, params=getTemplateParams(list(
      header=decoded_template_def$header, 
      title=title,
      content=content, 
      warning_msg=warning_msg, 
      buttons=buttons,
      footer=decoded_template_def$footer
    )))
    current_item = getNextDebugItem(response, questions)
    if(is.null(current_item)) {
      warning_msg = paste0("Item id ",response$item_debug," not found!")
      current_item = 1
    }
  }
}
score = list(theta=theta, sem=sem)
rm(questions)
