getContent = function(question) {
  o = question$response_options
  if(is.character(o)) { o = fromJSON(o) }
  options = c()

  if(length(o) > 0) {
    if(randomize_response_options == "1") {
      options = o[sample(1:length(o))]
    } else {
      options = o
    }
  }
  question$response_options = options;
  question$correct = NULL

  params = list(
    paragraph = paragraph,
    question = question,
    response_styles = paste0("width: ",response_width)
  )
  content = concerto.template.join(templateId=CAT_template,params=params)

  return(list(content=content,params=params))
}

shouldStop = function(response, sem, out, questions, test_time_limited, test_time_limit, test_time_left, item_time_limited, item_time_limit, item_time_left, timeout_when_not_full_item_time) {
  #run out of test time
  if(test_time_limited && test_time_left <= 0) { return(3) }
  if(timeout_when_not_full_item_time && test_time_limited && item_time_limited && test_time_left < item_time_limit) { return(3) }

  sr_count = fromJSON(stopping_item_count)
  if((sr_count$enabled == "1" && length(out) >= as.numeric(sr_count$count)) || length(out) >= dim(questions)[1]) { return(1) }

  sr_accuracy = fromJSON(stopping_accuracy)
  if(sr_accuracy$enabled == "1" && as.numeric(sr_accuracy$accuracy) >= sem ) { return(2) }
  return(0)
}

