getNextItemIndex = function(param_bank, theta, out, next_item_criterion, scoring_method, randomesque, cb_group, cb_control, questions) {
  current_index = length(out) + 1
  questions_num = dim(questions)[1]
  if(questions_num > 0) {
    for(i in 1:questions_num) {
      fixed_index = questions[i,"fixed_index"]
      if(!is.na(fixed_index) && !is.null(fixed_index) && fixed_index != ""){
        if(fixed_index == current_index) {
          return(i)
        } else {
          out = unique(c(out, i))
        }
      }
    }
  }
  return(nextItem(param_bank, theta=theta, out=out, criterion=next_item_criterion, method=scoring_method, randomesque=randomesque, cbGroup=cb_group, cbControl=cb_control)$item)
}

