      theta <- thetaEst(matrix(param_bank_out, ncol=4, byrow=F), correctness, method=scoring_method)
      print("sem")
      sem <- semTheta(theta, matrix(param_bank_out, ncol=4, byrow=F), correctness)
      print("response")
      response_id=saveResponse(response, question, rb, theta, sem, correct)

      #response processor
      if(!is.null(response_processor) && !is.na(response_processor) && response_processor != "") {
        concerto.test.run(response_processor, params=list(
          question=question,
          template_response=response,
          response_id=response_id,
          response_bank=rb,
          theta=theta,
          sem=sem,
          correct=correct,
          session=session
        ))
