      if(stopReason) {
        #out of time
        if(stopReason == 3) {
          .branch = "out_of_time"
        }
        break
      } else {
        if(item_time_limited) {
          item_time_left = item_time_limit
        }

        #next item processor
        if(!is.null(next_item_processor) && !is.na(next_item_processor) && next_item_processor != "") {
          current_item = concerto.test.run(next_item_processor, params=list(
            questions=questions,
            template_response=response,
            theta=theta,
            sem=sem,
            out=out,
            next_item_criterion=next_item_criterion,
            scoring_method=scoring_method,
            randomesque=randomesque,
            cb_group=cb_group,
            cb_control=cb_control
          ))$next_item
        } else {
          current_item = getNextItemIndex(param_bank, theta, out, next_item_criterion, scoring_method, randomesque, cb_group, cb_control, questions)
        }

      }
    } else {
      warning_msg = no_answer_alert
    }
  } else {
    content = getDebugContent(content, question)

    params = list()
    if(!is.null(inserts) && is.list(inserts)) {
      params = inserts
    }
    params = append(params, content$params)
    params = append(params, list(
      header=decoded_template_def$header, 
      title=title,
      content=content$content, 
      warning_msg=warning_msg, 
      buttons=buttons,
      footer=decoded_template_def$footer
    ))

    response = concerto.template.show(decoded_template_def$layout, params=params)
    current_item = getNextDebugItem(response, questions)
    if(is.null(current_item)) {
      warning_msg = paste0("Item id ",response$item_debug," not found!")
      current_item = 1
    }
  }
}
