while(T) {
  question = as.list(questions[current_item,])
  content = getContent(question)

  time_limit = 0
  if(test_time_limited || item_time_limited) {
    if(test_time_limited) {
      time_limit = test_time_left
    }
    if(item_time_limited && item_time_left < test_time_left) {
      time_limit = item_time_left
    }
  }

  updateSavedState(questions, theta, sem, out, test_time_left, item_time_left, correctness, current_item, time_taken)
  if(item_debug == "0") {
    params = list()
    if(!is.null(inserts) && is.list(inserts)) {
      params = inserts
    }
    params = append(params, content$params)
    params = append(params, list(
