      buttons=buttons,
      footer=decoded_template_def$footer
    ))
    if(!is.null(content_params_processor) && !is.na(content_params_processor) && content_params_processor != "") {
      params = concerto.test.run(content_params_processor, params=list(
        question=question,
        params=params
      ))$params
    }

    response = concerto.template.show(decoded_template_def$layout, params=params, timeLimit=time_limit)
    warning_msg = ""

    time_taken = time_taken + as.numeric(response$timeTaken)
    if(test_time_limited) {
      test_time_left = test_time_left - as.numeric(response$timeTaken)
      if(response$isTimeout == "1") test_time_left = 0
    }
    if(item_time_limited) {
      item_time_left = item_time_left - as.numeric(response$timeTaken)
      if(response$isTimeout == "1") item_time_left = 0
    }

    valid = "response" %in% ls(response) && response$response != ""
    if(valid || response$isTimeout == "1") {
      correct = 0
      if(!is.null(response$response) && response$response == question$correct) { correct = 1 }
      correctness = c(correctness, correct)
      out = c(out, current_item)
      param_bank_out = param_bank
      if(dim(questions)[1] > 1) {
        param_bank_out = param_bank[out,]
      }
      print("theta")
      print(param_bank_out)
      theta <- thetaEst(matrix(param_bank_out, ncol=4, byrow=F), correctness, method=scoring_method)
      print("sem")
      sem <- semTheta(theta, matrix(param_bank_out, ncol=4, byrow=F), correctness)
      print("response")
      response_id=saveResponse(response, question, rb, theta, sem, correct)

      #response processor
      if(!is.null(response_processor) && !is.na(response_processor) && response_processor != "") {
        concerto.test.run(response_processor, params=list(
          question=question,
          template_response=response,
          response_id=response_id,
          response_bank=rb,
          theta=theta,
          sem=sem,
          correct=correct,
          session=session
        ))
      }

      stopReason = shouldStop(response, sem, out, questions, test_time_limited, test_time_limit, test_time_left, item_time_limited, item_time_limit, item_time_left, timeout_when_not_full_item_time)
      if(stopReason) {
        #out of time
        if(stopReason == 3) {
          .branch = "out_of_time"
        }
        break
      } else {
        if(item_time_limited) {
          item_time_left = item_time_limit
        }

        #next item processor
        if(!is.null(next_item_processor) && !is.na(next_item_processor) && next_item_processor != "") {
          current_item = concerto.test.run(next_item_processor, params=list(
            questions=questions,
            template_response=response,
            theta=theta,
            sem=sem,
            out=out,
            next_item_criterion=next_item_criterion,
            scoring_method=scoring_method,
            randomesque=randomesque,
            cb_group=cb_group,
            cb_control=cb_control
          ))$next_item
        } else {
          current_item = getNextItemIndex(param_bank, theta, out, next_item_criterion, scoring_method, randomesque, cb_group, cb_control, questions)
        }

      }
    } else {
      warning_msg = no_answer_alert
    }
  } else {
    content = getDebugContent(content, question)

    params = list()
    if(!is.null(inserts) && is.list(inserts)) {
      params = inserts
    }
    params = append(params, content$params)
    params = append(params, list(
