isValid = function(type, value, condition, form, response) {
  valid = T
  if(!is.null(response)) {
    env_vld = new.env()
    if(length(form) > 0) {
      for(i in 1:length(form)) {
        v = response[[form[[i]]$name]]
        assign(form[[i]]$name, v, envir=env_vld)
      }
    }

    res = try(eval(parse(text=condition), envir=env_vld), T)
    if(!is.null(res) && res == T) {
      valid = T
    }
    if(!is.null(res) && res == F) {
      valid = F
    }
  }
  return(valid)
}

getContent = function(paragraph, form, response) {
  content = paragraph
  if(length(form) > 0) {
    for(i in 1:length(form)) {
      type = form[[i]]$definition$type
      value = response[[form[[i]]$name]]
      if(is.null(value)) {
        value = form[[i]]$default
      }
      if(is.null(value)) {
        value = ""
      }
      condition = form[[i]]$validation
      valid = isValid(type, value, condition, form, response)
      id = paste0("form",i)
      name = form[[i]]$name
      label = form[[i]]$label
      control = ""

      if(type == "singleline") {
        control = concerto.template.join(templateId=form_field_input_template,params=list(id=id,name=name,type="text",value=value))
      } else if(type == "password") {
        control = concerto.template.join(templateId=form_field_input_template,params=list(id=id,name=name,type="password",value=value))
      } else if(type == "multiline") {
        control = concerto.template.join(templateId=form_field_multiline_template,params=list(id=id,name=name,type="password",value=value))
      } else if(type == "select") {
        control = concerto.template.join(templateId=form_field_select_template,params=list(id=id,name=name,options=toJSON(form[[i]]$definition$select_options)))
      } else if(type == "checkbox") {
        checked = ""
        if(value=="1") {
          checked = "checked"
        }
        control = concerto.template.join(templateId=form_field_checkbox_template,params=list(id=id,name=name,attrs=checked))
      } else if(type == "slider") {
        if(value == "") {
          value = "null";
        }
        control = concerto.template.join(templateId=form_field_slider_template,params=list(id=id,name=name,value=value,definition=toJSON(form[[i]]$definition)))
      }
        classes = ""
        if(!is.null(response) && !valid) {
          classes = "validationHighlight"
        }
        field = concerto.template.join(templateId=form_field_template,params=list(id=id,label=label,classes=classes,control=control))
        content = paste0(content, field)
        }
        }
        return(content)
        }

